import { useEffect, useRef, useState } from 'react';
import Message from './Message';
import Item from './Item';
import './item.css';
import Features from './Features';
import AddItems from './AddItem';

export default function Items() {
    // useStates, useRefs HOOKS
    const [tasksData, setTasksData] = useState([]);
    const [isBeAdded, setIsBeAdded] = useState(false);
    const [isBeDeleted, setIsBeDeleted] = useState(false);
    const previousLength = useRef(0);

    // Hnadle Funcitons
    function handleDeleteTask(){
        setTasksData(currEl)
    }
    // useEffects Hooks
    useEffect(() => {
        const length = tasksData.length;

        if (length) {
            if (previousLength.current < length) {
                setIsBeDeleted(false);
                setIsBeAdded(true);
            }else{
                setIsBeAdded(false);
                setIsBeDeleted(true);
            }
        }

        previousLength.current = length;
    }, [tasksData]);

    return (
        <main>
            <section>
                {isBeAdded && (
                    <Message>
                        <p className="message add">
                            New task has been added to your todo list
                        </p>
                    </Message>
                )}
                {isBeDeleted && (
                    <Message>
                        <p className="message add">
                            A task has been deleted from your todo list
                        </p>
                    </Message>
                )}
                <AddItems onAddTasks={setTasksData} />
                <div className="items">
                    <ul>
                        {tasksData.map((currEl) => (
                            <Item task={currEl} key={currEl.id} />
                        ))}
                    </ul>
                </div>
                <Features />
            </section>
        </main>
    );
}
