export default function Item({
    task,
    onDeleteTask,
    onCompletedTask,
    id,
    setId,
}) {
    return (
        <>
            {id !== task.id ? (
                <li
                    className={`todoTask ${
                        task.isCompleted ? 'completedTask' : ''
                    }`}
                >
                    <p
                        className={`todoTaskName ${
                            task.isCompleted ? 'completed' : ''
                        }`}
                    >
                        <span>{task.task}</span>
                    </p>
                    <div className="icons">
                        <span className="icon edit" onClick={setId}>
                            <i className="ri-edit-fill"></i>
                        </span>
                        <span
                            className={`icon complete-button ${
                                task.isCompleted ? 'completedBtn' : ''
                            }`}
                            onClick={onCompletedTask}
                        >
                            <i className="ri-check-line"></i>
                        </span>
                        <span
                            className="icon delete-button"
                            onClick={onDeleteTask}
                        >
                            <i className="ri-delete-bin-fill"></i>
                        </span>
                    </div>
                </li>
            ) : (
                <form className="myForm newTaskName">
                    <label>
                        <input
                            type="text"
                            className="taskInput"
                            placeholder="New task goes here..."
                            // value={task}
                            // onChange={(e) => setTask(e.target.value)}
                        />
                    </label>
                    <input type="submit" className="addBtn btn" value="Add" />
                </form>
            )}
        </>
    );
}
