import { useEffect, useRef, useState } from 'react';
import Message from './Message';
import Item from './Item';
import './item.css';
import Features from './Features';
import AddItems from './AddItem';

export default function Items({ tasksData, setTasksData }) {
    // useStates, useRefs HOOKS
    const [isBeAdded, setIsBeAdded] = useState(false);
    const [isBeDeleted, setIsBeDeleted] = useState(false);
    const previousLength = useRef(0);

    // Normal Variables
    const newTasksData = [...tasksData];

    // Hnadle Funcitons
    function handleDeleteTask(id) {
        setTasksData(tasksData.filter((currEl) => currEl.id !== id));
    }
    function handleComletedTask(id) {
        setTasksData(
            tasksData.map((currEl) =>
                currEl.id !== id
                    ? currEl
                    : { ...currEl, isCompleted: !currEl.isCompleted }
            )
        );
    }
    function handleEditTask(id, newTaskName) {
        setTasksData(
            tasksData.map((currEl) =>
                currEl.id !== id ? currEl : { ...currEl, task: newTaskName }
            )
        );
    }
    // function handleSortTasks(e, value) {
    //     e.preventDefault();
    //     if (value === 'description') {
    //         newTasksData.sort((a, b) => {
    //             const x = a.task.toLowerCase();
    //             const y = b.task.toLowerCase();
    //             if (x > y) return 1;
    //             else if (x < y) return -1;
    //         });
    //         console.log(newTasksData);
    //         setTasksData(newTasksData);
    //     } else if (value === 'completedTasks') {
    //         newTasksData.sort((a, b) => a.isCompleted - b.isCompleted);
    //         setTasksData(newTasksData);
    //     } else if (value === 'inputOrder') console.log('20');
    // }
    // function handleClear(e, value) {
    //     e.preventDefault();

    //     if (value === 'all') setTasksData([]);
    //     else if (value === 'completedTasks')
    //         setTasksData(newTasksData.filter((currEl) => !currEl.isCompleted));
    // }
    // useEffects Hooks
    useEffect(() => {
        const length = tasksData.length;
        if (length || isBeDeleted || isBeAdded) {
            if (previousLength.current < length) {
                setIsBeDeleted(false);
                setIsBeAdded(true);
            } else if (previousLength.current > length) {
                setIsBeAdded(false);
                setIsBeDeleted(true);
            }
        }

        previousLength.current = length;
    }, [tasksData]); // Error => when i add other dependencies
    return (
        <main>
            <section>
                {isBeAdded && (
                    <Message>
                        <p className="message add">
                            New task has been added to your todo list
                        </p>
                    </Message>
                )}
                {isBeDeleted && (
                    <Message>
                        <p className="message delete">
                            A task has been deleted from your todo list
                        </p>
                    </Message>
                )}
                <AddItems onAddTasks={setTasksData} />
                <div className="items">
                    <ul>
                        {tasksData.map((currEl) => (
                            <Item
                                task={currEl}
                                key={currEl.id}
                                onDeleteTask={() => handleDeleteTask(currEl.id)}
                                onCompletedTask={() =>
                                    handleComletedTask(currEl.id)
                                }
                            />
                        ))}
                    </ul>
                </div>
                <Features onSort={handleSortTasks} onClear={handleClear} />
            </section>
        </main>
    );
}
