import { useState } from 'react';
import Header from './header/Header';
import Item from './items/Items';
import Summary from './summary/Summary';

function App() {
    // useStates HOOKS
    const [tasksData, setTasksData] = useState([]);
    const tasksNumber = tasksData.length;
    const completedTaskNumber = tasksData.filter(
        (currEl) => currEl.isComleted
    ).length;

    return (
        <div className="container">
            <Header />
            <Item tasksData={tasksData} setTasksData={setTasksData} />
            <Summary tasksNumber={tasksNumber} completedTaskNumber/>
        </div>
    );
}

export default App;
