import { useEffect, useRef, useState } from 'react';
import Message from './Message';
import Item from './Item';
import './item.css';
import Features from './Features';
import AddItems from './AddItem';

export default function Items() {
    // useStates, useRefs HOOKS
    const [tasksData, setTasksData] = useState([]);
    const [isBeAdded, setIsBeAdded] = useState(false);
    const [isBeDeleted, setIsBeDeleted] = useState(false);
    const previousLength = useRef(0);

    // Hnadle Funcitons

    // useEffects Hooks
    useEffect(() => {
        const length = tasksData.length;
        
        if (length) {
            console.log(length);
            console.log(previousLength.current)
        }
        return ()=> previousLength.current = length;
    }, [tasksData]);

    return (
        <main>
            <section>
                <Message />
                <AddItems onAddTasks={setTasksData} />
                <div className="items">
                    <ul>
                        {tasksData.map((currEl) => (
                            <Item task={currEl} key={currEl.id} />
                        ))}
                    </ul>
                </div>
                <Features />
            </section>
        </main>
    );
}
