export default function Item({task, onDeleteTask, onCompletedTask}) {
    return (
        <li className={`todoTask ${task.isCompleted ? 'completedTask' : ''}`}>
            <p className="todoTaskName">
                <span>{task.task}</span>
            </p>
            <div className="icons">
                <span className="icon edit">
                    <i className="ri-edit-fill"></i>
                </span>
                <span className="icon complete-button" onClick={onCompletedTask}>
                    <i className="ri-check-line"></i>
                </span>
                <span className="icon delete-button" onClick={onDeleteTask}>
                    <i className="ri-delete-bin-fill"></i>
                </span>
            </div>
        </li>
    );
}
