import { useState } from "react";

export default function Item({task, onDeleteTask, onCompletedTask, onEditTask}) {
    const [newTaskName, setNewTaskName] = useState('')
    
    return (
        <li className={`todoTask ${task.isCompleted ? 'completedTask' : ''}`}>
            <p className={`todoTaskName ${task.isCompleted ? 'completed' : ''}`}>
                <span>{task.task}</span>
            </p>
            <div className="icons">
                <span className="icon edit">
                    <i className="ri-edit-fill"></i>
                </span>
                <span className={`icon complete-button ${task.isCompleted ? 'completedBtn' : ''}`} onClick={onCompletedTask}>
                    <i className="ri-check-line"></i>
                </span>
                <span className="icon delete-button" onClick={()=>onEditTask()}>
                    <i className="ri-delete-bin-fill"></i>
                </span>
            </div>
        </li>
    );
}
