import { useEffect, useState } from 'react';
import Message from './Message';
import Item from './Item';
import './item.css';
import Features from './Features';
import AddItems from './AddItem';

export default function Items() {
    // useStates HOOKS
    const [tasksData, setTasksData] = useState([]);

    // Hnadle Funcitons

    // useEffects Hooks
    useEffect(() => {
        if (tasksData.length) {
            if (pre)
            console.log('ckeck');
        }

        return ()=>{
            const previousLength = tasksData.length;
        }
    }, [tasksData]);

    return (
        <main>
            <section>
                <Message />
                <AddItems onAddTasks={setTasksData} />
                <div className="items">
                    <ul>
                        {tasksData.map((currEl) => (
                            <Item task={currEl} key={currEl.id}/>
                        ))}
                    </ul>
                </div>
                <Features />
            </section>
        </main>
    );
}
